    document.getElementById("tablaMov2020").rows[1].cells[2].textContent = pc
    document.getElementById("tablaMov2020").rows[1].cells[3].textContent = pap

    nroPerodo += 1
    //escribo marzo
    pbu = pbu * coeficienteMovilidad[nroPerodo]
    pc = pc * coeficienteMovilidad[nroPerodo]
    pap = pap * coeficienteMovilidad[nroPerodo]
    supl = 1500 * porcent
